import { useState } from "react";
import { Component } from "react";

const DIM = 8; //8x8 Board

//Checks if a character is Uppercase
function IsUpperCase(value) {
    return value.charCodeAt(0) >= 65 && value.charCodeAt(0) <= 90
}

//Checks if a character is Lowercase
function IsLowerCase(value) {
    return value.charCodeAt(0) >= 97 && value.charCodeAt(0) <= 122
}

//Checks if a character is a Digit
function IsDigit(value) {
    return value.charCodeAt(0) >= 48 && value.charCodeAt(0) <= 57
}

function ConvertFENtoPiece(letter) {
    switch (letter.toLowerCase()) {
        case "p":
            return 'pawn'
        case "n":
            return "knight"
        case "k":
            return "king";
        case "q":
            return "queen";
        case "r":
            return "rook";
        case "b":
            return "bishop";
        default:
            return "";
    }
}

function ConvertFENPieceToPiece(value) {
    if (IsUpperCase(value)) {
        return `white-${ConvertFENtoPiece(value)}`;
    }

    if (IsLowerCase(value)) {
        return `black-${ConvertFENtoPiece(value)}`;
    }
}

class Square extends Component {
    constructor(props) {
        super(props)
        this.x = props.x
        this.y = props.y
        this.fenPiece = props.piece
        this.color = props.color
        this.piece = ConvertFENPieceToPiece(this.fenPiece)
    }

    onClick = function () {
        //TODO: Implement clicking a square
    }

    render() {
        let styles = ['chessSquare', 'piece'];
        styles.push(this.color);
        styles.push(this.piece);
        return <div className={styles.filter(Boolean).join(" ")}></div>
    }
}

class Board extends Component {
    constructor(props) {
        super(props)
        this.state = {
            position: [
                'rnbqkbnr',
                'pppppppp',
                '8',
                '8',
                '8',
                '8',
                'PPPPPPPP',
                'RNBQKBNR'
            ]
        };
        this.setSelectedPiece = props.setSelectedPiece;
    }

    render() {
        const squares = [];
        let squareColorIsWhite = true;
        for (let i = 0; i < DIM; i++) {
            for (let j = 0; j < DIM; j++) {
                const piece = this.state.position[i].charAt(j);
                let color = squareColorIsWhite ? "" : "dark"
                squares.push(<Square key={`${i}-${j}`} x={i} y={j} piece={piece} color={color} />);
                squareColorIsWhite = !squareColorIsWhite
            }
            squareColorIsWhite = !squareColorIsWhite
        }
        return (
            <div className="chessGrid">
                {squares}
            </div>
        );
    }
}

export default function Chess() {

    const [selectedPiece, setSelectedPiece] = useState('');

    return (<>
        <div className="chessContainer">
            <div id="grid" className="chessGrid">
                <Board setSelectedPiece={setSelectedPiece} />
            </div>
        </div>
    </>)
}
