import { useState } from "react";

function Square({ dark, color, piece }) {
    let c = "chessSquare piece";
    c = dark ? c + ' dark' : c;
    let pieceToAdd = color ? 'white-' : 'dark-';
    pieceToAdd += piece;
    c += ' ' + pieceToAdd;
    return <div className={c}></div>
}

function Row({ val }) {
    let squares = [];

    for (let i = 0; i < 8; i++) {
        let dark = i % 2 == val ? true : false;
        squares.push(<Square dark={dark} piece={'pawn'} color={true}></Square>)
    }

    return squares;
}

function Board() {
    let rows = [];

    for (let i = 0; i < 8; i++) {
        let v = i % 2 == 0 ? 1 : 0;
        rows.push(<Row val={v}></Row>)
    }

    return rows;
}

export default function Chess() {

    const [boardPosition, setBoardPosition] = useState(
        [
            ['rnbqkbnr'],
            ['pppppppp'],
            ['8'],
            ['8'],
            ['8'],
            ['8'],
            ['PPPPPPPP'],
            ['RNBQKBNR']
        ])

    return (<>
        <div className="chessContainer">
            <div className="chessGrid">
                <Board></Board>
            </div>
        </div>
    </>)
}
