import { useState } from "react";

function Square({ dark, piece, x, y, move }) {
    let c = "chessSquare piece";
    c = dark ? c + ' dark' : c;

    let p, cl;

    if (piece != undefined) {
        c = c + ' ' + piece;
        p = piece.split('-')[1][0];
        p = p == 'k' ? (piece.split('-')[1][1] == 'n' ? 'n' : p) : p;
        cl = piece.split('-')[0][0] == 'w' ? true : false;
    }

    return <div className={c} x={x} y={y} onClick={() => move(p, cl, x, y)}></div>
}

function Board({ position, move }) {

    let color;
    let piece;
    let rows = [];
    let key = 1;
    let white = true;
    let x = 0;
    let y = 0;

    for (let i = 0; i < position.length; i++) {
        y = 0;
        for (let j = 0; j < position[i].length; j++) {
            if (position[i].charCodeAt(j) >= 97 && position[i].charCodeAt(j) <= 122) {
                color = 'black';
            } else if (position[i].charCodeAt(j) >= 65 && position[i].charCodeAt(j) <= 90) {
                color = 'white';
            } else {
                color = undefined;
            }

            switch (position[i][j].toLowerCase()) {
                case "p":
                    piece = 'pawn';
                    break;
                case 'n':
                    piece = 'knight';
                    break;
                case 'k':
                    piece = 'king';
                    break;
                case 'q':
                    piece = 'queen';
                    break;
                case 'b':
                    piece = 'bishop';
                    break;
                case 'r':
                    piece = 'rook';
                    break;
                default:
                    piece = undefined;
                    break;
            }

            if (piece != undefined && color != undefined) {
                rows.push(<Square dark={white ? false : true} key={key} piece={color + '-' + piece} x={x} y={y} move={move}></Square>)
                if (key % 8 != 0) {
                    white = !white;
                }
                key++
                y++;
            } else {
                for (let k = 0; k < Number(position[i][j]); k++) {
                    rows.push(<Square dark={white ? false : true} key={key} x={x} y={y} move={move}></Square>)
                    if (key % 8 != 0) {
                        white = !white;
                    }
                    key++;
                    y++
                }
            }
        }
        x++;
    }

    return rows;
}

export default function Chess() {

    const [boardPosition, setBoardPosition] = useState(
        [
            'rnbqkbnr',
            'pppppppp',
            '8',
            '8',
            '8',
            '8',
            'PPPPPPPP',
            'RNBQKBNR'
        ]);

    const [selectedPiece, setSelectedPiece] = useState('');
    const [oldX, setOldX] = useState('');
    const [oldY, setOldY] = useState('');
    const [allowedMoves, setAllowedMoves] = useState([])

    function convertFenToString(fen) {
        for (let i = 0; i < row.length; i++) {
            if (row.charCodeAt(i) >= 48 && row.charCodeAt(i) <= 57) {
                newRow += ' '.repeat(Number(row[i]))
            } else {
                newRow += row[i];
            }
        }
    }

    function Move(p, c, x, y) {
        if (selectedPiece) {

            //allows to deselect a piece
            if (x == oldX) {
                if (y == oldY) {
                    setSelectedPiece('')
                    return
                }
            }

            let row = boardPosition[x];
            let newRow = '';
            let oldRow = '';
            let allowed = false;

            //check if the move you're making is legal
            for (let i = 0; i < allowedMoves.length; i++) {
                let move = allowedMoves[i];

                if (x == move[0]) {
                    if (y == move[1]) {
                        allowed = true;
                    }
                }
            }

            //stop if illegal move is used
            if (!allowed) {
                return
            }

            //convert FEN to string and update the new position
            for (let i = 0; i < row.length; i++) {
                if (row.charCodeAt(i) >= 48 && row.charCodeAt(i) <= 57) {
                    newRow += ' '.repeat(Number(row[i]))
                } else {
                    newRow += row[i];
                }
            }

            newRow = newRow.substring(0, y) + selectedPiece + newRow.substring(y + 1, newRow.length);

            //convert the new position to FEN
            for (let i = newRow.length - 1; i >= 0; i--) {
                if (newRow[i] == ' ') {
                    if (i < newRow.length - 1) {
                        if (newRow.charCodeAt(i + 1) >= 48 && newRow.charCodeAt(i + 1) <= 57) {
                            newRow = newRow.substring(0, i) + (Number(newRow[i + 1]) + 1).toString() + newRow.substring(i + 1, newRow.length);
                            newRow = newRow.slice(0, i + 1) + newRow.slice(i + 2);
                        } else {
                            newRow = newRow.substring(0, i) + '1' + newRow.substring(i + 1, newRow.length);
                        }
                    } else {
                        newRow = newRow.substring(0, i) + '1' + newRow.substring(i + 1, newRow.length);
                    }
                }
            }

            //get the old position; if same row, get the current new position
            row = x == oldX ? newRow : boardPosition[oldX];

            //convert FEN to string and update the old position
            for (let i = 0; i < row.length; i++) {
                if (row.charCodeAt(i) >= 48 && row.charCodeAt(i) <= 57) {
                    oldRow += ' '.repeat(Number(row[i]))
                } else {
                    oldRow += row[i];
                }
            }

            oldRow = oldRow.substring(0, oldY) + ' ' + oldRow.substring(oldY + 1, oldRow.length);

            //convert old position to FEN
            for (let i = oldRow.length - 1; i >= 0; i--) {
                if (oldRow[i] == ' ') {
                    if (i < oldRow.length - 1) {
                        if (oldRow.charCodeAt(i + 1) >= 48 && oldRow.charCodeAt(i + 1) <= 57) {
                            oldRow = oldRow.substring(0, i) + (Number(oldRow[i + 1]) + 1).toString() + oldRow.substring(i + 1, oldRow.length);
                            oldRow = oldRow.slice(0, i + 1) + oldRow.slice(i + 2);
                        } else {
                            oldRow = oldRow.substring(0, i) + '1' + oldRow.substring(i + 1, oldRow.length);
                        }
                    } else {
                        oldRow = oldRow.substring(0, i) + '1' + oldRow.substring(i + 1, oldRow.length);
                    }
                }
            }

            //create a new board position and update the board
            let newBoard = [];

            for (let i = 0; i < boardPosition.length; i++) {
                if (i == x || i == oldX) {
                    //use the old position if it's the same row as the old position will be updating the current newPosition and will have the most up-to-date values
                    if (x == oldX) {
                        newBoard.push(oldRow);
                    }
                    else if (i == x) {
                        newBoard.push(newRow);
                    } else if (i == oldX) {
                        newBoard.push(oldRow);
                    }
                } else {
                    newBoard.push(boardPosition[i]);
                }
            }

            //update the board and reset the variables
            setBoardPosition(newBoard);
            setOldX('')
            setOldY('')
            setSelectedPiece('')

        } else {
            switch (p) {
                case "p":
                    if (c) {
                        if (x == 6) {
                            setAllowedMoves([[x - 1, y], [x - 2, y]])
                        } else {
                            setAllowedMoves([[x - 1, y]])
                        }
                    } else {
                        if (x == 1) {
                            setAllowedMoves([[x + 1, y], [x + 2, y]])
                        } else {
                            setAllowedMoves([[x + 1, y]])
                        }
                    }
                    break;
                default:
                    break;
            }

            if (p != undefined && c != undefined) {
                p = c ? p.toUpperCase() : p;

                setSelectedPiece(p);
                setOldX(x);
                setOldY(y);
            }
        }
    }

    return (<>
        <div className="chessContainer">
            <div id="grid" className="chessGrid">
                <Board position={boardPosition} move={Move}></Board>
            </div>
        </div>
    </>)
}
