import { useState } from "react";

function Digit({ value, enterDigit }) {
    return <button class="digit" onClick={enterDigit}>{value}</button>
}

function Row({ digits, setEquation, equation }) {
    return (
        <>
            <div class="board-row">
                <Digit value={digits[0]} enterDigit={() => setEquation(equation + '' + digits[0])}></Digit>
                <Digit value={digits[1]} enterDigit={() => setEquation(equation + '' + digits[1])}></Digit>
                <Digit value={digits[2]} enterDigit={() => setEquation(equation + '' + digits[2])}></Digit>
            </div>
        </>
    )
}

export default function Calculator() {

    const [equation, setEquation] = useState("");

    setEquation(() => {

    })

    return (
        <>
            <input type="text" value={equation} onChange={e => setEquation(e.target.value)} />
            <Row digits={[7, 8, 9]} setEquation={setEquation} equation={equation}></Row>
            <Row digits={[4, 5, 6]} setEquation={setEquation} equation={equation}></Row>
            <Row digits={[1, 2, 3]} setEquation={setEquation} equation={equation}></Row>
            <Digit value={0} enterDigit={() => handleZeros()}></Digit>
        </>
    );
}
