import { Component } from "react"

function ConvertFENPieceToPiece(value) {
    if (IsUpperCase(value)) {
        return `white-${ConvertFENtoPiece(value)}`;
    }

    if (IsLowerCase(value)) {
        return `black-${ConvertFENtoPiece(value)}`;
    }

    return "";
}

function IsLowerCase(value) {
    return value.charCodeAt(0) >= 97 && value.charCodeAt(0) <= 122
}

function IsUpperCase(value) {
    return value.charCodeAt(0) >= 65 && value.charCodeAt(0) <= 90
}

function ConvertFENtoPiece(letter) {
    switch (letter.toLowerCase()) {
        case "p":
            return 'pawn'
        case "n":
            return "knight"
        case "k":
            return "king";
        case "q":
            return "queen";
        case "r":
            return "rook";
        case "b":
            return "bishop";
        default:
            return "";
    }
}

export class Square extends Component {
    constructor(props) {
        super(props)
        this.x = props.x
        this.y = props.y
        this.peice = props.piece
        this.color = props.color
        this.setSelectedPiece = props.setSelectedPiece
        this.selectedPiece = props.selectedPiece
        this.piece = ConvertFENPieceToPiece(this.fenPiece)
        this.updatePossibleMoves = props.updatePossibleMoves
    }

    onClick = () => {
        console.log(this.selectedPiece)
        if (this.selectedPiece) {
            this.setSelectedPiece()
            this.updatePossibleMoves([])
        } else {
            this.setSelectedPiece(this.piece)
        }
        this.props.getPossibleMoves(this)
    }

    render() {
        let styles = ['chessSquare', 'piece'];
        styles.push(this.color);
        styles.push(this.piece);

        if (this.props.possibleMoves.some(move => move.x === this.x && move.y === this.y)) {
            styles.push("possibleMove");
        }

        return <div onClick={this.onClick} className={styles.filter(Boolean).join(" ")}></div>
    }
}
