import { Component } from "react"

export class Square extends Component {
    constructor(props) {
        super(props)
        this.x = props.x
        this.y = props.y
        this.piece = props.piece
        this.color = props.color
        this.updatePossibleMoves = props.updatePossibleMoves
        this.state = {
            setSelectedPiece: props.setSelectedPiece,
            selectedPiece: props.selectedPiece
        }
    }

    onClick = () => {
        console.log("Before IF", this.state.selectedPiece)
        if (this.state.selectedPiece) {
            this.state.setSelectedPiece()
            this.state.updatePossibleMoves([])
            console.log("Inside IF", this.state.selectedPiece)
        } else {
            console.log("Inside ELSE", this.state.selectedPiece)
            this.state.setSelectedPiece(this.piece)
        }
        console.log("After IF", this.state.selectedPiece)
        this.props.getPossibleMoves(this)
    }

    render() {
        let styles = ['chessSquare', 'piece'];
        styles.push(this.color);
        styles.push(this.piece);

        if (this.props.possibleMoves.some(move => move.x === this.x && move.y === this.y)) {
            styles.push("possibleMove");
        }

        return <div onClick={this.onClick} className={styles.filter(Boolean).join(" ")}></div>
    }
}
