import { useState, useEffect } from "react";
import { Component } from "react";

const DIM = 8; //8x8 Board

//Checks if a character is Uppercase
function IsUpperCase(value) {
    return value.charCodeAt(0) >= 65 && value.charCodeAt(0) <= 90
}

//Checks if a character is Lowercase
function IsLowerCase(value) {
    return value.charCodeAt(0) >= 97 && value.charCodeAt(0) <= 122
}

//Checks if a character is a Digit
function IsDigit(value) {
    return value.charCodeAt(0) >= 48 && value.charCodeAt(0) <= 57
}

function ConvertFENtoPiece(letter) {
    switch (letter.toLowerCase()) {
        case "p":
            return 'pawn'
        case "n":
            return "knight"
        case "k":
            return "king";
        case "q":
            return "queen";
        case "r":
            return "rook";
        case "b":
            return "bishop";
        default:
            return "";
    }
}

function ConvertFENPieceToPiece(value) {
    if (IsUpperCase(value)) {
        return `white-${ConvertFENtoPiece(value)}`;
    }

    if (IsLowerCase(value)) {
        return `black-${ConvertFENtoPiece(value)}`;
    }
}

function ConvertFenToString(value) {

}

class Square extends Component {
    constructor(props) {
        super(props)
        this.x = props.x
        this.y = props.y
        this.fenPiece = props.piece
        this.color = props.color
        this.piece = ConvertFENPieceToPiece(this.fenPiece)
    }

    onClick = () => {
        this.props.setSelectedPiece(this.piece)
        this.props.getPossibleMoves(this)
    }

    render() {
        let styles = ['chessSquare', 'piece'];
        styles.push(this.color);
        styles.push(this.piece);
        return <div onClick={this.onClick} className={styles.filter(Boolean).join(" ")}></div>
    }
}

class Board extends Component {
    constructor(props) {
        super(props);
        this.state = {
            position: [
                'rnbqkbnr',
                'pppppppp',
                '8',
                '8',
                '8',
                '4P3',
                'PPPPPPPP',
                'RNBQKBNR'
            ]
        };
        this.setSelectedPiece = props.setSelectedPiece;
    }

    getPossibleMoves(square) {
        const squares = this.renderSquares();
        let possibleMoves = [];

        if (square.piece) {
            let color = square.piece.split('-')[0];
            let piece = square.piece.split('-')[1];
            switch (piece) {
                case "pawn":
                    if (color === "white") {
                        const targetSquare1 = squares.find(s => s.props.x === square.x - 1 && s.props.y === square.y);

                        if (targetSquare1 && !targetSquare1.props.piece) {
                            possibleMoves.push({ x: square.x - 1, y: square.y });
                        }

                        const targetSquare2 = squares.find(s => s.props.x === square.x - 2 && s.props.y === square.y);

                        if (square.x === 6 && targetSquare1 && !targetSquare2.props.piece) {
                            possibleMoves.push({ x: square.x - 2, y: square.y });
                        }
                    }
                    break;
                case "rook":
                    break;
                case "bishop":
                    break;
                case "knight":
                    break;
                case "king":
                    break;
                case "queen":
                    break;
                default:
                    break;
            }
        }

        console.log("Possible Moves:", possibleMoves);
    }

    renderSquares() {
        let squareColorIsWhite = true;
        const squares = [];
        for (let i = 0; i < DIM; i++) {
            let row = ConvertFenToString(this.state.position[i]);
            for (let j = 0; j < DIM; j++) {
                const piece = row.charAt(j);
                let color = squareColorIsWhite ? "" : "dark";
                squares.push(
                    <Square
                        getPossibleMoves={this.getPossibleMoves.bind(this)}
                        key={`${i}-${j}`}
                        x={i}
                        y={j}
                        piece={piece}
                        color={color}
                        setSelectedPiece={this.setSelectedPiece}
                    />
                );
                squareColorIsWhite = !squareColorIsWhite;
            }
            squareColorIsWhite = !squareColorIsWhite;
        }
        return squares;
    }

    render() {
        const squares = this.renderSquares();
        return (
            <div className="chessGrid">
                {squares}
            </div>
        );
    }
}


export default function Chess() {

    const [selectedPiece, setSelectedPiece] = useState('');

    function updateSelectedPiece(value) {
        setSelectedPiece(value);
    }

    // useEffect(() => {
    //     console.log(selectedPiece);
    // }, [selectedPiece]);

    return (<>
        <div className="chessContainer">
            <div id="grid" className="chessGrid">
                <Board setSelectedPiece={updateSelectedPiece} />
            </div>
        </div>
    </>)
}
