import { useState } from "react";
import { Component } from "react";

const DIM = 8; //8x8 Board

class Square extends Component {
    constructor(props, x, y, piece, color) {
        super(props)
        this.x = x
        this.y = y
        this.piece = piece
        this.color = color
    }

    onClick = function () {
        //TODO: Implement clicking a square
    }

    render() {
        return <div className={'chessSquare piece'}></div>
    }
}

class Board extends Component {
    constructor(props) {
        super(props)
        this.position = []

        for (let i = 0; i < DIM; i++) {
            this.position[i] = [];

            for (let j = 0; j < DIM; j++) {
                this.position[i].push(new Square(i, j))
            }
        }
    }

    render() {
        const squares = [];
        for (let i = 0; i < DIM; i++) {
            for (let j = 0; j < DIM; j++) {
                const piece = this.state.position[i][j];
                const color = (i + j) % 2 === 0 ? 'white' : 'black';
                squares.push(<Square key={`${i}-${j}`} x={i} y={j} piece={piece} color={color} />);
            }
        }
        return (
            <div className="chessGrid">
                {squares}
            </div>
        );
    }
}

export default function Chess() {

    const [boardPosition, setBoardPosition] = useState(
        [
            'rnbqkbnr',
            'pppppppp',
            '8',
            '8',
            '8',
            '8',
            'PPPPPPPP',
            'RNBQKBNR'
        ]);

    return (<>
        <div className="chessContainer">
            <div id="grid" className="chessGrid">
                {new Board()}
            </div>
        </div>
    </>)
}
