import { useState } from "react";

function Square({ dark, piece }) {
    let c = "chessSquare piece";
    c = dark ? c + ' dark' : c;
    c = c + ' ' + piece;
    return <div className={c}></div>
}

function Row({ val }) {
    let squares = [];

    for (let i = 0; i < 8; i++) {
        let dark = i % 2 == val ? true : false;
        squares.push(<Square dark={dark} piece={'pawn'} color={true}></Square>)
    }

    return squares;
}

function Board({ position }) {

    let color;
    let piece;
    let rows = [];
    let key = 0;

    for (let i = 0; i < position.length; i++) {
        for (let j = 0; j < position[i].length; j++) {
            if (position[i].charCodeAt(j) >= 97 && position[i].charCodeAt(j) <= 122) {
                color = 'black';
            } else if (position[i].charCodeAt(j) >= 65 && position[i].charCodeAt(j) <= 90) {
                color = 'white';
            } else {
                color = undefined;
            }

            switch (position[i][j].toLowerCase()) {
                case "p":
                    piece = 'pawn';
                    break;
                case 'n':
                    piece = 'knight';
                    break;
                case 'k':
                    piece = 'king';
                    break;
                case 'q':
                    piece = 'queen';
                    break;
                case 'b':
                    piece = 'bishop';
                    break;
                case 'r':
                    piece = 'rook';
                    break;
                default:
                    piece = undefined;
                    break;
            }

            if (piece != undefined && color != undefined) {
                rows.push(<Square dark={key % 2 == 0 ? true : false} key={key} piece={color + '-' + piece}></Square>)
            } else {
                rows.push(<Square dark={key % 2 == 0 ? true : false} key={key}></Square>)
            }

            key++;
        }
    }

    return rows;
}

export default function Chess() {

    const [boardPosition, setBoardPosition] = useState(
        [
            'rnbqkbnr',
            'pppppppp',
            '8',
            '8',
            '8',
            '8',
            'PPPPPPPP',
            'RNBQKBNR'
        ]);

    return (<>
        <div className="chessContainer">
            <div className="chessGrid">
                <Board position={boardPosition}></Board>
            </div>
        </div>
    </>)
}
