import { useState } from "react";

function Digit({ value, enterDigit }) {
    return <button className="digit" onClick={() => enterDigit({ value })}>{value}</button>
}

function Row({ digits, updateInput }) {
    return (
        <>
            <div className="board-row">
                {digits.map((digit) => (
                    <Digit key={digit} value={digit} enterDigit={updateInput}></Digit>
                ))}
            </div>
        </>
    )
}

export default function Calculator() {

    const [equation, setEquation] = useState("");

    function updateInput({ value }) {
        if (equation.length == 1) {

        }


        setEquation(equation + '' + value)
    }

    return (
        <>
            <input type="text" value={equation} onChange={e => setEquation(e.target.value)} />
            <Row digits={[7, 8, 9]} updateInput={updateInput}></Row>
            <Row digits={[4, 5, 6]} updateInput={updateInput}></Row>
            <Row digits={[1, 2, 3]} updateInput={updateInput}></Row>
            <Digit value={0} enterDigit={updateInput}></Digit>
        </>
    );
}
