import { Component } from "react";
import { Square } from "./Square.js"
import { ConvertFENPieceToPiece, ConvertFenToString } from "./HelperMethods.js";

const DIM = 8; //8x8 Board

export class Board extends Component {
    constructor(props) {
        super(props);
        this.state = {
            position: [
                'rnbqkbnr',
                'pppppppp',
                '8',
                '8',
                '8',
                '8',
                'PPPPPPPP',
                'RNBQKBNR'
            ],
            possibleMoves: [],
            selectedSquareX: "",
            selectedSquareY: ""
        };
    }

    updatePossibleMoves = (moves) => {
        this.setState({ possibleMoves: moves });
    };

    updateSelectedPiece = (piece) => {
        this.setState({ selectedPiece: piece })
    }

    getPossibleMoves(square) {
        const squares = this.renderSquares();
        let possibleMoves = [];

        if (square.piece) {
            let color = square.piece.split('-')[0];
            let piece = square.piece.split('-')[1];
            switch (piece) {
                case "pawn":
                    if (color === "white") {
                        //move one square ahead
                        const targetSquare1 = squares.find(s => s.props.x === square.x - 1 && s.props.y === square.y);
                        if (targetSquare1 && !targetSquare1.props.piece) {
                            possibleMoves.push({ x: square.x - 1, y: square.y });
                        }

                        //move two squares on the very first move
                        const targetSquare2 = squares.find(s => s.props.x === square.x - 2 && s.props.y === square.y);
                        if (square.x === 6 && targetSquare2 && !targetSquare2.props.piece && !targetSquare1.props.piece) {
                            possibleMoves.push({ x: square.x - 2, y: square.y });
                        }

                        //capture to the left diagonal
                        const leftNeigborToPotentuallyCapture1 = squares.find(s => s.props.x === square.x - 1 && s.props.y === square.y - 1);
                        if (leftNeigborToPotentuallyCapture1 && leftNeigborToPotentuallyCapture1.props.piece &&
                            ConvertFENPieceToPiece(leftNeigborToPotentuallyCapture1.props.piece).split('-')[0] != square.piece.split("-")[0]) {
                            possibleMoves.push({ x: square.x - 1, y: square.y - 1 });
                        }

                        //capture to the right diagonal
                        const leftNeigborToPotentuallyCapture2 = squares.find(s => s.props.x === square.x - 1 && s.props.y === square.y + 1);
                        if (leftNeigborToPotentuallyCapture2 && leftNeigborToPotentuallyCapture2.props.piece &&
                            ConvertFENPieceToPiece(leftNeigborToPotentuallyCapture2.props.piece).split('-')[0] != square.piece.split("-")[0]) {
                            possibleMoves.push({ x: square.x - 1, y: square.y + 1 });
                        }

                        //TODO: Implement the french fancy move - En passant
                        //TODO: Implement promotion
                    } else {
                        const targetSquare1 = squares.find(s => s.props.x === square.x + 1 && s.props.y === square.y);
                        if (targetSquare1 && !targetSquare1.props.piece) {
                            possibleMoves.push({ x: square.x + 1, y: square.y });
                        }

                        //move two squares on the very first move
                        const targetSquare2 = squares.find(s => s.props.x === square.x + 2 && s.props.y === square.y);
                        if (square.x === 1 && targetSquare2 && !targetSquare2.props.piece && !targetSquare1.props.piece) {
                            possibleMoves.push({ x: square.x + 2, y: square.y });
                        }

                        //capture to the left diagonal
                        const leftNeigborToPotentuallyCapture1 = squares.find(s => s.props.x === square.x + 1 && s.props.y === square.y - 1);
                        if (leftNeigborToPotentuallyCapture1 && leftNeigborToPotentuallyCapture1.props.piece &&
                            ConvertFENPieceToPiece(leftNeigborToPotentuallyCapture1.props.piece).split('-')[0] != square.piece.split("-")[0]) {
                            possibleMoves.push({ x: square.x + 1, y: square.y - 1 });
                        }

                        //capture to the right diagonal
                        const leftNeigborToPotentuallyCapture2 = squares.find(s => s.props.x === square.x + 1 && s.props.y === square.y + 1);
                        if (leftNeigborToPotentuallyCapture2 && leftNeigborToPotentuallyCapture2.props.piece &&
                            ConvertFENPieceToPiece(leftNeigborToPotentuallyCapture2.props.piece).split('-')[0] != square.piece.split("-")[0]) {
                            possibleMoves.push({ x: square.x + 1, y: square.y + 1 });
                        }

                        //TODO: Implement the french fancy move - En passant
                        //TODO: Implement promotion
                    }
                    break;
                case "rook":

                    break;
                case "bishop":
                    break;
                case "knight":
                    break;
                case "king":
                    break;
                case "queen":
                    break;
                default:
                    break;
            }
        }

        console.log("Possible Moves:", possibleMoves);
        this.updatePossibleMoves(possibleMoves);
    }

    renderSquares() {
        let squareColorIsWhite = true;
        const squares = [];
        for (let i = 0; i < DIM; i++) {
            let row = ConvertFenToString(this.state.position[i]);
            for (let j = 0; j < DIM; j++) {
                const piece = row.charAt(j);
                let color = squareColorIsWhite ? "" : "dark";
                squares.push(
                    <Square
                        getPossibleMoves={this.getPossibleMoves.bind(this)}
                        key={`${i}-${j}`}
                        x={i}
                        y={j}
                        piece={ConvertFENPieceToPiece(piece.trim())}
                        color={color}
                        selectedPiece={this.state.selectedPiece}
                        setSelectedPiece={this.updateSelectedPiece}
                        possibleMoves={this.state.possibleMoves}
                        updatePossibleMoves={this.updatePossibleMoves}
                    />
                );
                squareColorIsWhite = !squareColorIsWhite;
            }
            squareColorIsWhite = !squareColorIsWhite;
        }
        return squares;
    }

    render() {
        const squares = this.renderSquares();
        return (
            <div className="chessGrid">
                {squares}
            </div>
        );
    }
}
