import { useState } from "react";

function submitValue() {
    toDoItems.push(document.getElementById("value").value);
}

function Button(value) {
    const [toDoItems, addToDoItem] = useState("");


    function handleClick(value) {
        addToDoItem(toDoItems.push())
    }

    return (
        <button type="submit" onClick={handleClick}>{value}</button>
    );
}

const toDoItems = [];

export default function ToDo() {
    const todoList = toDoItems.map(item =>
        <li>
            {item}
        </li>)

    return (
        <>
            <input type="text" placeholder="Add new" id="value" />
            <Button value="Add" />
            <ul>{todoList}</ul>
        </>
    );
}
