import { useState } from "react";
import { Component } from "react";

const DIM = 8; //8x8 Board

function IsLowerCase(value) {
    value.charCodeAt(j) >= 65 && position[i].charCodeAt(j) <= 90
}

class Square extends Component {
    constructor(props, x, y, piece) {
        super(props)
        this.x = x
        this.y = y
        this.piece = piece
        this.color = IsLowerCase(piece) ? "dark" : '';
    }

    onClick = function () {
        //TODO: Implement clicking a square
    }

    render() {
        return <div className={`chessSquare piece ${this.color}`}></div>
    }
}

class Board extends Component {
    constructor(props) {
        super(props)
        this.state = {
            position: [
                'rnbqkbnr',
                'pppppppp',
                '8',
                '8',
                '8',
                '8',
                'PPPPPPPP',
                'RNBQKBNR'
            ]
        };
    }

    render() {
        const squares = [];
        for (let i = 0; i < DIM; i++) {
            for (let j = 0; j < DIM; j++) {
                const piece = this.state.position[i][j];
                squares.push(<Square key={`${i}-${j}`} x={i} y={j} piece={piece} />);
            }
        }
        return (
            <div className="chessGrid">
                {squares}
            </div>
        );
    }
}

export default function Chess() {

    const [boardPosition, setBoardPosition] = useState(
        [
            'rnbqkbnr',
            'pppppppp',
            '8',
            '8',
            '8',
            '8',
            'PPPPPPPP',
            'RNBQKBNR'
        ]);

    return (<>
        <div className="chessContainer">
            <div id="grid" className="chessGrid">
                <Board />
            </div>
        </div>
    </>)
}
