import { Component } from "react"

export class Square extends Component {
    constructor(props) {
        super(props)
        this.x = props.x
        this.y = props.y
        this.piece = props.piece
        this.color = props.color
        this.updatePossibleMoves = props.updatePossibleMoves
        this.setSelectedSquareX = props.setSelectedSquareX
        this.setSelectedSquareY = props.setSelectedSquareY
    }

    onClick = () => {
        console.log(this.props.selectedSquareX, this.props.selectedSquareY, this.props.possibleMoves)
        if (this.props.selectedSquareX == this.x && this.props.selectedSquareY == this.y) {
            this.setSelectedSquareX("")
            this.setSelectedSquareY("")
            this.updatePossibleMoves([])
        } else {
            this.setSelectedSquareX(this.x)
            this.setSelectedSquareY(this.y)
            if (this.props.possibleMoves.some(m => m.x == this.props.selectedSquareX && m.y == this.props.selectedSquareY)) {
                console.log("MOVE!!!")
            } else {
                this.props.getPossibleMoves(this)
            }
        }
    }

    render() {
        let styles = ['chessSquare', 'piece'];
        styles.push(this.color);
        styles.push(this.piece);

        if (this.props.possibleMoves.some(move => move.x === this.x && move.y === this.y)) {
            styles.push("possibleMove");
        }

        return <div onClick={this.onClick} className={styles.filter(Boolean).join(" ")}></div>
    }
}
