import { useState } from "react";

function submitValue() {
    toDoItems.push(document.getElementById("value").value);
}

function Button() {
    return (
        <button type="submit">Add</button>
    )
}

const toDoItems = [];

export default function ToDo() {
    const todoList = toDoItems.map(item =>
        <li>
            {item}
        </li>)

    return (
        <>
            <input type="text" placeholder="Add new" id="value" />
            <input type="submit" value="Add" onClick={submitValue} />
            <ul>{todoList}</ul>
        </>
    );
}
